import React, { useState } from "react";
import { loadContributors } from "../logic/contributorsActions";

function SearchForm() {
  const [contributorsList, setContributorsList] = useState(null);
  const [error, setError] = useState(null);

  console.log("there is the contributors in DASHBOARD", contributorsList);

  const [repoName, setRepoName] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    setContributorsList([]);
    loadContributors(repoName).then(setContributorsList).catch(setError);
  }

  return (
    <form>
      <input
        placeholder="Repository name"
        onChange={(event) => setRepoName(event.target.value.trim())}
      ></input>
      <button
        type="submit"
        onClick={(event) => {
          handleSubmit(event);
        }}
      >
        Get collab!
      </button>
    </form>
  );
}

export default SearchForm;
